package zplgfa_test

import (
	"bytes"
	"encoding/base64"
	"fmt"
	"image"
	_ "image/png"
	"log"
	"simonwaldherr.de/go/zplgfa"
	"strings"
)

func ExampleCompressASCII() {
	str := zplgfa.CompressASCII("FFFFFFFF000000")
	fmt.Print(str)

	// Output: NFL0
}

func ExampleConvertToZPL() {
	// base64 encoded image file (./tests/test6.png)
	str := ``

	// decode base64 encoded image
	data, err := base64.StdEncoding.DecodeString(str)
	if err != nil {
		fmt.Println("error:", err)
		return
	}

	// load image head information from bytes reader
	config, format, err := image.DecodeConfig(bytes.NewReader(data))
	if err != nil {
		log.Printf("Warning: image not compatible, format: %s, config: %v, error: %s\n", format, config, err)
		return
	}

	// load and decode image from bytes reader
	img, _, err := image.Decode(bytes.NewReader(data))
	if err != nil {
		log.Printf("Warning: could not decode the file, %s\n", err)
		return
	}

	// convert image to zpl string
	zplstr := zplgfa.ConvertToZPL(img, zplgfa.CompressedASCII)

	// remove whitespace - only for the test
	zplstr = strings.Replace(zplstr, " ", "", -1)
	zplstr = strings.Replace(zplstr, "\n", "", -1)

	fmt.Println(zplstr)

	// Output: ^XA,^FS^FO0,0^GFA,52,51,3,FFFF00::FE3F00::FFFF00FFE300::FFFF00E22300::FFFF00::^FS,^XZ
}
